export function getUniqueWords(): string[] {
  let wordSet = new Set<string>();
  for (const glossary of glossaries) {
    wordSet.add(glossary.letter);
  }
  return Array.from(wordSet).sort();
}

export function filterByInitialLetter(letter: string): Glossary[] {
  return glossaries.filter(
    (item) => item.word[0].toUpperCase() === letter.toUpperCase(),
  );
}
